import "strings"

func GetStateShort(fullState string) string {
	fullState = strings.ToLower(strings.TrimSpace(fullState))
	for k, v := range StateWithShortMapping {
		aa := strings.ToLower(k)
		if aa == fullState {
			return v
		}
	}
	return ""
}

